function start(){bindEventListeners(document.getElementsByClassName("board")[0].children)}function bindEventListeners(e){for(var t=0;t<e.length;t++)e[t].addEventListener("contextmenu",makeGreen),e[t].addEventListener("click",makeBlue),e[t].addEventListener("dblclick",hide)}function updateCounts(){for(var e={blue:0,green:0,invisible:0},t=document.getElementsByClassName("board")[0].children,n=0;n<t.length;n++)t[n].classList.contains("blue")?e.blue++:t[n].classList.contains("green")?e.green++:t[n].classList.contains("invisible")&&e.invisible++;displayTotals(e)}function makeGreen(e){e.preventDefault(),e.target.classList.toggle("green"),updateCounts()}function makeBlue(e){e.target.classList.toggle("blue"),updateCounts()}function hide(e){e.target.classList.toggle("invisible"),updateCounts()}function displayTotals(e){for(var t in e)document.getElementById(t+"-total").innerHTML=e[t]}document.addEventListener("DOMContentLoaded",start);